import React from "react";
import styles from "./List.module.scss";
import { confirmAlert } from "react-confirm-alert"; // Import
import "react-confirm-alert/src/react-confirm-alert.css"; // Import css

import {
  FaExpandArrowsAlt,
  FaCompressArrowsAlt,
  FaTrashAlt
} from "react-icons/fa";

class List extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      opened: false
    };
    this.close = this.close.bind(this);
    this.handleClickDiv = this.handleClickDiv.bind(this);
    this.handleDeleteClick = this.handleDeleteClick.bind(this);
  }
  handleClickDiv(e) {
    this.props.closeLists();
    if (
      !e.target.closest("." + styles["delete-container"]) &&
      (!this.state.opened || e.target.closest("." + styles["icon-container"]))
    ) {
      this.setState({
        opened: !this.state.opened
      });
    }
  }
  handleDeleteClick(e) {
    confirmAlert({
      title: "Are you sure?",
      message: `This action will remove #${this.props.id} list permanently.`,
      buttons: [
        {
          label: "Yes",
          onClick: () => this.removeList()
        },
        {
          label: "No"
        }
      ]
    });
  }
  removeList() {
    fetch(this.props.base_url + "lists/" + this.props.id, {
      method: "DELETE",
      headers: this.props.authorization_header
    }).then(response => {
      if (response.status === 204) {
        this.props.listRemoved(this.props.id);
      } else {
        console.log(response);
      }
    });
  }
  close() {
    this.setState({
      opened: false
    });
  }
  render() {
    return (
      <div
        className={
          styles["list-container"] +
          " " +
          (this.state.opened && styles["opened"])
        }
        onClick={this.handleClickDiv}>
        <div className={styles["icon-container"]}>
          {this.state.opened ? <FaCompressArrowsAlt /> : <FaExpandArrowsAlt />}
        </div>
        <div
          className={styles["delete-container"]}
          onClick={this.handleDeleteClick}>
          <FaTrashAlt />
        </div>
        <strong>{this.props.title}</strong>
        <span>
          Due date:{" "}
          {this.props.due_date
            ? new Date(this.props.due_date).toDateString()
            : "<never>"}
        </span>
        <span>
          Items: {`${this.props.items_done_count}/${this.props.items_count}`}
        </span>
        {this.props.description && (
          <p>
            Description: {this.props.description.substr(0, 120)}
            {this.props.description.length > 120 && "..."}
          </p>
        )}
        {this.state.opened && (
          <div>
            <h1>opened</h1>
          </div>
        )}
      </div>
    );
  }
}
export default List;
