import React from "react";

import styles from "./Login.module.scss";

class Login extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      url: this.props.base_url + "auth/login"
    };
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleSubmit(e) {
    e.preventDefault();
    const data = new FormData(e.target);
    const json_data = {
      auth: {
        email: data.get("email"),
        password: data.get("password")
      }
    };
    fetch(this.state.url, {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(json_data)
    })
      .then(function(response) {
        return response.json();
      })
      .then(data => {
        if (data.token) {
          saveLoginData(data);
          if (this.props.onSuccess) this.props.onSuccess(data);
        } else {
          if (this.props.onError) {
            if (data.error === "unauthorized") {
              this.props.onError("Invalid credentials.");
            } else {
              this.props.onError("An unknown error has occurred.");
            }
            console.log(data);
          }
        }
      });
  }
  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <div className={styles["input-container"]}>
            <label htmlFor="email">Email:</label>
            <input type="email" name="email" id="login-email" />
          </div>
          <div className={styles["input-container"]}>
            <label htmlFor="password">Password:</label>
            <input
              type="password"
              name="password"
              minLength="6"
              maxLength="20"
              id="login-password"
            />
          </div>
          <input type="submit" value="Login" />
        </form>
      </div>
    );
  }
}
export default Login;

const saveLoginData = data => {
  localStorage.setItem("token", data.token);
  localStorage.setItem("username", data.username);
  localStorage.setItem("token_expires", data.exp);
  localStorage.setItem("user_id", data.user_id);
};
